import React, {useEffect, useState} from 'react';
import BackendErrorMessages from 'components/BackendErrorMessages';

const ArticleForm = ({onSubmit, errors, initialValues}) => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [description, setDescription] = useState('');
    const [tagList, setTagList] = useState('');



    const handleSubmit = (e) => {
        const article = {
            title, body, description, tagList
        };
        e.preventDefault();
        onSubmit(article);

    };

    useEffect(() => {
        if (!initialValues) {
            return;
        }
        setTitle(initialValues.title)
        setBody(initialValues.body)
        setDescription(initialValues.description)
        setTagList(initialValues.tagList.join(' '))
    }, [initialValues])


    return <div className="editor-page">
        <div className="container page">
            <div className="row">
                <div className="com-md-10 offset-md-1 col-xs-12">
                    <BackendErrorMessages backendErrors={errors}/>
                    <form onSubmit={handleSubmit}>
                        <fieldset>
                            <fieldset className="form-group">
                                <input className="form-control form-control-lg" type="text" placeholder="Title"
                                       value={title} onChange={e => setTitle(e.target.value)}/>
                            </fieldset>
                            <fieldset className="form-group">
                                <input className="form-control form-control-lg" type="text"
                                       placeholder="What's this article about?" value={body}
                                       onChange={e => setBody(e.target.value)}/>
                            </fieldset>
                            <fieldset className="form-group">
                                <textarea className="form-control" type="text"
                                          placeholder="Write your article?"
                                          rows="8"
                                          value={description}
                                          onChange={e => setDescription(e.target.value)}/>
                            </fieldset>
                            <fieldset className="form-group">
                                <input className="form-control form-control-lg" type="text"
                                       placeholder="Enter Tags"
                                       value={tagList} onChange={e => setTagList(e.target.value)}/>
                            </fieldset>
                            <fieldset className="form-group">
                                <button className="btn btn-lg pull-xs-right btn-primary">Publish Article</button>
                            </fieldset>

                        </fieldset>

                    </form>
                </div>
            </div>
        </div>
    </div>;

};

export default ArticleForm;