import React, {useEffect} from 'react'
import useFetch from 'hooks/useFetch'
import UserArticle from 'components/UserArticle'
import {NavLink} from "react-router-dom";

const ProfilePage = ({match, location}) => {
    const slug = match.params.slug;

    const apiUrl = `/profiles/${slug}`;
    const [{response}, doFetch] = useFetch(apiUrl);

    useEffect(() => {
        doFetch()
    }, [doFetch])

    if (!response) {
        return null
    }
    const username = response.profile.username

    return <div className="profile-page">
        <div className="user-info">
            <div className="container">
                <div className="row">
                    <div className="col-xs-12 col-md-10 offset-md-1">
                        <img className="user-img" src={response.profile.image} alt=""/>
                        <h4>{username}</h4>
                        <p>{response.profile.bio}</p>
                        <button className="btn btn-sm action-btn btn-outline-secondary">
                            <i className="ion-plus-round"/> &nbsp; Follow {username}</button>
                    </div>
                </div>
            </div>
        </div>
        <div className="container">
            <div className="row">
                <div className="col-xs-12 col-md-10 offset-md-1">
                    <div className="articles-toggle">
                        <ul className="nav nav-pills outline-active">
                            <li className="nav-item">
                                <NavLink className="nav-link" to={`/profiles/${username}`} exact>My Posts</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink className="nav-link" to={`/profiles/${username}/favorites`}>Favorited
                                    Posts</NavLink>
                            </li>
                        </ul>
                    </div>
                    <UserArticle
                        location={location}
                        username={username}
                        url={match.url}
                    />
                </div>
            </div>
        </div>
    </div>
}

export default ProfilePage