import React from 'react';
import {Switch, Route} from 'react-router-dom';
import GlobalFeed from 'pages/globalFeed/globalFeed';
import Article from 'pages/article/article';
import Authentication from 'pages/authentication/Authentication.jsx';
import TagFeed from 'pages/TagFeed/tagFeed';
import YourFeed from 'pages/yourFeed/yourFeed';
import ArticleCreate from 'pages/articleCreate/articleCreate';
import ArticleEdit from 'pages/articleEdit/articleEdit';
import Settings from 'components/Settings';


export default () => {

    return (
        <Switch>
            <Route path="/" component={GlobalFeed} exact/>
            <Route path="/settings" component={Settings} />
            <Route path="/articles/new" component={ArticleCreate}/>
            <Route path="/articles/:slug/edit" component={ArticleEdit}/>
            <Route path="/tags/:slug" component={TagFeed}/>
            <Route path="/feed" component={YourFeed} exact/>
            <Route path="/articles/:slug" component={Article}/>
            <Route path="/login" component={Authentication}/>
            <Route path="/register" component={Authentication}/>
        </Switch>
    );
};